            <i className="fab fa-instagram fa-2x" />
          </a>
          <a href="#">
            <i className="fab fa-github fa-2x" />
          </a>
        </div>
        <div className="copyright">Â© Copyright 2023</div>
      </footer>
    );
};

export default Footer;
